var PVES=function(){function t(e,t){$.ajax({url:e,type:"get",dataType:"json",success:t})}var e={};e.provSelect="#prov-select";e.munSelect="#mun-select";e.defaultProvText="Provincia";e.defaultMunText="Municipio";e.init=function(){if(typeof jQuery=="undefined"){console.error("Error: Jquery is not defined!");return}t("provincias/index.json",function(t){$(e.provSelect).append($("<option>").text(e.defaultProvText).attr("value",0));$(e.munSelect).append($("<option>").text(e.defaultMunText).attr("value",0));$.each(t,function(t,n){$(e.provSelect).append($("<option>").text(n.name).attr("value",n.code))})});$(e.provSelect).on("change",function(){var n=this.value;url="provincias/"+n+".json",t(url,function(t){var n=t.municipios;$(e.munSelect).empty();$.each(n,function(t,n){$(e.munSelect).append($("<option>").text(n.name).attr("value",n.code))})})})};return e}()